sys_graphql_resolver.script:
(function process( /*ResolverEnvironment*/ env) {
    var userId = env.getArguments().id;
	if(!userId) {
		try {
			// If the "@source" directive is used in the schema for a child element
			// then getSource() returns that value
			// e.g. "manager.value" on a reference field on User will return a sys id
			userId = env.getSource();
			gs.info("JGL: 1 " + userId);
		} catch (exc) { gs.error("JGL: 1"); } // try/catch is a workaround for a glitch
	}

    var gr = new GlideRecord('sys_user');
    gr.query('sys_id', userId);

    return gr;
})(env);
